import React, { useState } from "react";
import { Data } from "./Data";
import DishMenu from "./DishMenu";
const Dish = () => {
  const [dishData, setDishData] = useState(Data);
  const filterfnc = (cat) => {
    const updateDish = Data.filter((val) => {
      return val.category === cat;
    });
    setDishData(updateDish);
  };
  return (
    <>
      <div className="mt-3 border-danger border-bottom">
        <h1 className="text-center fw-normal text-danger text-capitalize">
          Order your favourite dish here
        </h1>
      </div>
      <div className="container mt-3">
        <div className="d-flex justify-content-around">
          <button className="btn btn-primary" onClick={() => setDishData(Data)}>
            All
          </button>
          <button
            className="btn btn-primary"
            onClick={() => filterfnc("Breakfast")}
          >
            Breakfast
          </button>
          <button
            className="btn btn-primary"
            onClick={() => filterfnc("Evening")}
          >
            Evening
          </button>
          <button
            className="btn btn-primary"
            onClick={() => filterfnc("Dinner")}
          >
            Dinner
          </button>
          <button
            className="btn btn-primary"
            onClick={() => filterfnc("Drinks")}
          >
            Drinks
          </button>
        </div>
      </div>
      <DishMenu dishData={dishData} />
    </>
  );
};

export default Dish;
