import React, { useState } from "react";
import { Data } from "./Data";
import { DishCard } from "./DishCard";
const Dish = () => {
  const [dishData, setDishData] = useState(Data);
  const filterfnc = (cat) => {
    const updateDish = Data.filter((val) => {
      return val.category === cat;
    });
    setDishData(updateDish);
  };
  return (
    <>
      <div className="mt-3 border-danger border-bottom">
        <h1 className="text-center fw-normal text-danger text-capitalize">
          Order your favourite dish here
        </h1>
      </div>
      <div className="container mt-3">
        <div className="d-flex justify-content-around">
          <button
            className="btn btn-primary"
            onClick={() => filterfnc("Breakfast")}
          >
            Breakfast
          </button>
          <button
            className="btn btn-primary"
            onClick={() => filterfnc("Evening")}
          >
            Evening
          </button>
          <button
            className="btn btn-primary"
            onClick={() => filterfnc("Dinner")}
          >
            Dinner
          </button>
          <button
            className="btn btn-primary"
            onClick={() => filterfnc("Drinks")}
          >
            Drinks
          </button>
          <button
            className="btn btn-primary"
            onClick={() => filterfnc("Breakfast")}
          >
            All
          </button>
        </div>
      </div>
      <div className="container-fluid">
        <div className="row">
          <div className="col-10 p-0 col-md-10 mx-auto mt-5">
            <div className="row p-0 ">
              {dishData.map((val, indx) => {
                const { image, name, category, price, description } = val;
                return (
                  <DishCard
                    key={indx}
                    image={image}
                    name={name}
                    category={category}
                    price={price}
                    description={description}
                  />
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Dish;
